package com.college.mca.result.service;

import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestClient;
import org.springframework.http.HttpHeaders;

@Service
@RequiredArgsConstructor
public class RestClientService {
    private final  String BASE_URL ="https://erpapi.manit.ac.in/api/fetch_register";
    private final String TOKEN ="Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..OHANtfHYeLkdWpXqROzJxkjllnkJpnmY1JHpELQ2RVM";
    private final RestClient restClient;

    public void fetchJuniorsResult() {
        try {
            String jsonResp = restClient.get()
                    .uri(BASE_URL)
                    .header(HttpHeaders.AUTHORIZATION, TOKEN)
                    .retrieve()
                    .body(String.class);
            System.out.println(jsonResp);
        } catch (Exception ex) {
            System.out.println("Error fetching results: " + ex);
        }
    }
}
